mapper.select:
Inner join sys_role_dept srd on srd.dept_id = dept.dept_id Inner join sys_role role on role.role_id = srd.role_id Inner join sys_role_user sur on sur.role_id = role.role_id
WHERE
dept.parent_id=#{parentId}
(dept.parent_id = 0 or dept.parent_id=(select dept_id from sys_dept where parent_id= 0))
and sur.user_id = #{userId}
